import React, { Component } from "react";
//글쓰기 위한 editor 사용
import CKEditor from "ckeditor4-react";
import { Button, Form } from "react-bootstrap";
import axios from "axios";

axios.defaults.withCredentials = true;
const headers = { withCredentials: true };

class BoardWriteForm extends Component {
  state = {
    data: "",
  };
  writeBoard = () => {
    let url;
    let send_param;

    const boardTitle = this.boardTitle.value;
    const boardContent = this.state.data;

    if (boardTitle === undefined || boardTitle === "") {
      alert("글 제목을 입력해주세요.");
      boardTitle.focus();
      return;
    } else if (boardContent === undefined || boardContent === "") {
      alert("글 내용을 입력해주세요.");
      boardContent.focus();
      return;
    }

    url = "http://localhost:8080/board/write";
    send_param = {
      headers,
      _id: window.sessionStorage.getItem("login_id"),
      title: boardTitle,
      content: boardContent,
    };

    axios
      .post(url, send_param)
      .then((returnData) => {
        if (returnData.data.message) {
          alert(returnData.data.message);
          window.location.href = "/";
        } else {
          alert("글쓰기 실패");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  componentDidMount() {
    console.log(this.props.location.query);
  }
  render() {
    const divStyle = {
      margin: 50,
    };
    const titleStyle = {
      marginBottom: 5,
    };
    const buttonStyle = {
      marginTop: 5,
    };
    return (
      <div style={divStyle} className="App">
        <h2>글쓰기</h2>
        <Form.Control
          type="text"
          style={titleStyle}
          placeholder="글 제목"
          ref={(ref) => (this.boardTitle = ref)}
        >
          <CKEditor
            data={this.state.data}
            onChange={this.onEditorChange}
          ></CKEditor>
          <Button style={buttonStyle} onClick={this.writeBoard} block>
            저장하기
          </Button>
        </Form.Control>
      </div>
    );
  }
}
export default BoardWriteForm;
