import React, { Component } from "react";
import { Form, Button } from "react-bootstrap";
import axios from "axios";
axios.defaults.withCredentials = true;
const headers = { withCredentials: true };

class MypageForm extends Component {
  update = () => {
    //현재 비밀번호 일치하는지
    const name = this.name.value;

    const cur_pw = this.cur_pw.value;
    const new_pw = this.new_pw.value;
    const new_pw_Check = this.new_pw_Check.value;

    if (name === "" || name === undefined) {
      alert("바꿀 이름을 입력해주세요.");
      this.name.focus();
      return;
    } else if (cur_pw === "" || cur_pw === undefined) {
      alert("현재 비밀번호를 입력해주세요.");
      this.cur_pw.focus();
      return;
    }

    //new_pw없으면 name만 수정
    if (new_pw === "" || new_pw === undefined) {
      const send_param = {
        name: name,
        _id: window.sessionStorage.getItem("login_id"),
      };
      axios
        .post("http://localhost:8080/member/update", send_param)
        .then((returnData) => {
          if (returnData.data.message) {
            alert("회원정보 수정 완료했습니다.");
            this.name = "";
            this.cur_pw = "";
            this.new_pw = "";
            this.new_pw_Check = "";
          } else {
            alert("회원정보 수정 실패! 다시 시도해주세요.");
          }
        });
    }

    //바꿀비밀번호, 확인 일치하는지
    else {
      if (new_pw !== new_pw_Check) {
        alert("바꿀 비밀번호를 다시 확인해주세요.");
        this.new_pw.focus();
        return;
      }
      //바꿀 비밀번호 체크 일치
      else {
        const send_param = {
          name: name,
          _id: window.sessionStorage.getItem("login_id"),
          new_pw: new_pw,
        };
        axios
          .post("http://localhost:8080/member/update", send_param)
          .then((returnData) => {
            if (returnData.data.message) {
              alert("회원정보 수정 완료했습니다.");
              this.name = "";
              this.cur_pw = "";
              this.new_pw = "";
              this.new_pw_Check = "";
            } else {
              alert("회원정보 수정 실패! 다시 시도해주세요.");
            }
          });
      }
    }
  };
  render() {
    const divStyle = {
      margin: 50,
    };
    const marginBottom = {
      marginBottom: 5,
    };
    return (
      <>
        <div style={divStyle}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>email</Form.Label>
            <Form.Control
              type="email"
              disabled
              value={window.sessionStorage.getItem("login_email")}
            />
            <Form.Label>name</Form.Label>
            <Form.Control type="text" placeholder="Enter email" />
            <Form.Label>password</Form.Label>
            <Form.Control type="password" placeholder="Enter password" />
            <Form.Label>new password</Form.Label>
            <Form.Control type="password" placeholder="Enter New Password" />
            <Form.Label>new password check</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter New Password Check"
            />
          </Form.Group>
          <Button variant="primary" block style={marginBottom} onClick={uate}>
            회원정보 수정
          </Button>
          <Button variant="primary" block>
            회원 탈퇴
          </Button>
        </div>
      </>
    );
  }
}

export default MypageForm;
